function initMsnry(){
	if (!isMobile.any && document.querySelectorAll('.card_wrapper').length > 0) {
		var msnry = [];
		var msnryArr = document.querySelectorAll('.card_wrapper');

		for(var i=0;i<msnryArr.length;i++){
			(function(e){
				msnry.push(new Masonry(e, {
					itemSelector: '.card',
					percentPosition: true,
					transitionDuration: '0s'
				}));
			})(msnryArr[i]);
		}
		function msnryReload(){
			msnry.forEach(function(e){e.layout()});
		}
		document.addEventListener("mouseenter", msnryReload);
		document.addEventListener("mouseleave", msnryReload);
		document.addEventListener("scroll", msnryReload);
	}
}